import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TextInput,
  TouchableOpacity,
  KeyboardAvoidingView,
  Platform,
  Modal,
  Animated,
  Dimensions,
  PanResponder,
  Alert,
  ScrollView,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { SafeAreaView as SafeAreaViewRN } from 'react-native-safe-area-context';
import { RouteProp, useRoute } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { useNavigation } from '@react-navigation/native';
import { useApp } from '../contexts/AppContext';
import { useAI } from '../contexts/AIContext';
import { RootStackParamList } from '../types/navigation';
import { Case, Message, CaseNote } from '../types';
import Icon from 'react-native-vector-icons/FontAwesome';
import { patientGreetings } from '../data/mockData';

const { width: SCREEN_WIDTH, height: SCREEN_HEIGHT } = Dimensions.get('window');
const PANEL_WIDTH = SCREEN_WIDTH * 0.85;

type ChatNavigationProp = StackNavigationProp<RootStackParamList, 'Chat'>;
type ChatRouteProp = RouteProp<RootStackParamList, 'Chat'>;

const ChatScreen: React.FC = () => {
  const route = useRoute<ChatRouteProp>();
  const navigation = useNavigation<ChatNavigationProp>();
  const { state, dispatch, addMessage, generateId } = useApp();
  const { generateResponse } = useAI();
  const { caseId } = route.params;

  const [currentCase, setCurrentCase] = useState<Case | null>(null);
  const [messageInput, setMessageInput] = useState('');
  const [questionsCount, setQuestionsCount] = useState(0);
  const [showToolsMenu, setShowToolsMenu] = useState(false);
  const [showNotesModal, setShowNotesModal] = useState(false);
  const [showTestsModal, setShowTestsModal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [newNote, setNewNote] = useState('');
  const [noteType, setNoteType] = useState<'symptom' | 'observation' | 'general'>('general');
  const flatListRef = useRef<FlatList>(null);

  // Panel lateral animaci贸n
  const panelPosition = useRef(new Animated.Value(-PANEL_WIDTH)).current;
  const overlayOpacity = useRef(new Animated.Value(0)).current;
  const [isPanelOpen, setIsPanelOpen] = useState(false);

  // PanResponder para swipe
  const panResponder = useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => false,
      onMoveShouldSetPanResponder: (_, gestureState) => {
        // Solo activar si es swipe horizontal hacia la izquierda desde el borde derecho
        return gestureState.dx < -10 && Math.abs(gestureState.dy) < 50;
      },
      onPanResponderMove: (_, gestureState) => {
        if (gestureState.dx < 0 && !isPanelOpen) {
          const newPosition = Math.max(-PANEL_WIDTH, gestureState.dx);
          panelPosition.setValue(newPosition);
          overlayOpacity.setValue(Math.abs(newPosition) / PANEL_WIDTH * 0.5);
        }
      },
      onPanResponderRelease: (_, gestureState) => {
        if (gestureState.dx < -100) {
          openPanel();
        } else {
          closePanel();
        }
      },
    })
  ).current;

  const openPanel = () => {
    setIsPanelOpen(true);
    Animated.parallel([
      Animated.spring(panelPosition, {
        toValue: 0,
        useNativeDriver: true,
        tension: 65,
        friction: 11,
      }),
      Animated.timing(overlayOpacity, {
        toValue: 0.5,
        duration: 300,
        useNativeDriver: true,
      }),
    ]).start();
  };

  const closePanel = () => {
    Animated.parallel([
      Animated.spring(panelPosition, {
        toValue: -PANEL_WIDTH,
        useNativeDriver: true,
        tension: 65,
        friction: 11,
      }),
      Animated.timing(overlayOpacity, {
        toValue: 0,
        duration: 200,
        useNativeDriver: true,
      }),
    ]).start(() => setIsPanelOpen(false));
  };

  // Cargar caso actual
  useEffect(() => {
    const case_ = state.cases.find(c => c.id === caseId);
    if (case_) {
      setCurrentCase(case_);
      setQuestionsCount(case_.questionsThisSession || 0);

      // Si no hay mensajes, a帽adir saludo inicial
      if (case_.messages.length === 0) {
        addInitialMessage(case_);
      }
    }
  }, [caseId, state.cases]);

  // Desplazar al 煤ltimo mensaje
  useEffect(() => {
    if (currentCase && currentCase.messages.length > 0) {
      setTimeout(() => {
        flatListRef.current?.scrollToEnd({ animated: true });
      }, 100);
    }
  }, [currentCase?.messages]);

  // A帽adir mensaje inicial
  const addInitialMessage = async (case_: Case) => {
    const greeting = patientGreetings[Math.floor(Math.random() * patientGreetings.length)];
    const initialMessage: Message = {
      id: generateId(),
      text: greeting,
      sender: 'patient',
      timestamp: new Date(),
      type: 'text',
    };

    addMessage(case_.id, initialMessage);

    // Marcar como activo
    dispatch({
      type: 'UPDATE_CASE',
      payload: {
        id: case_.id,
        updates: { status: 'active' },
      },
    });
  };

  // Enviar mensaje
  const sendMessage = async () => {
    if (!messageInput.trim() || !currentCase || questionsCount >= 5 || isLoading) {
      return;
    }

    setIsLoading(true);

    // A帽adir mensaje del usuario
    const userMessage: Message = {
      id: generateId(),
      text: messageInput,
      sender: 'user',
      timestamp: new Date(),
      type: 'text',
    };

    addMessage(currentCase.id, userMessage);
    setMessageInput('');
    const newCount = questionsCount + 1;
    setQuestionsCount(newCount);

    // Actualizar contador en el caso
    dispatch({
      type: 'UPDATE_CASE',
      payload: {
        id: currentCase.id,
        updates: { questionsThisSession: newCount },
      },
    });

    try {
      // Generar respuesta con IA
      const aiResponse = await generateResponse(messageInput, currentCase);

      const patientMessage: Message = {
        id: generateId(),
        text: aiResponse.response,
        sender: 'patient',
        timestamp: new Date(),
        type: 'text',
        score: aiResponse.score,
        color: aiResponse.color,
      };

      addMessage(currentCase.id, patientMessage);

      // Actualizar rapport y sessionId
      const rapportChange = aiResponse.score >= 70 ? 2 : aiResponse.score >= 50 ? 0 : -3;
      dispatch({
        type: 'UPDATE_CASE',
        payload: {
          id: currentCase.id,
          updates: {
            sessionId: aiResponse.sessionId,
            rapport: Math.max(0, Math.min(100, (currentCase.rapport || 70) + rapportChange)),
          },
        },
      });

    } catch (error) {
      console.error('Error generando respuesta:', error);

      // Respuesta de fallback
      const fallbackMessage = generateFallbackResponse(messageInput);
      const patientMessage: Message = {
        id: generateId(),
        text: fallbackMessage,
        sender: 'patient',
        timestamp: new Date(),
        type: 'text',
      };

      addMessage(currentCase.id, patientMessage);
    }

    setIsLoading(false);
  };

  // Respuesta de fallback
  const generateFallbackResponse = (message: string): string => {
    const msg = message.toLowerCase();

    if (msg.includes('c贸mo') || msg.includes('qu茅')) {
      return 'Es complicado... necesito pensarlo m谩s.';
    } else if (msg.includes('por qu茅')) {
      return 'No estoy seguro/a de las razones.';
    } else if (msg.includes('cu谩ndo')) {
      return 'Hace tiempo que vengo sintiendo esto.';
    } else {
      return 'Gracias por preguntar. Me ayuda hablar de esto.';
    }
  };

  // A帽adir nota
  const addNote = () => {
    if (!newNote.trim() || !currentCase) return;

    const note: CaseNote = {
      id: generateId(),
      text: newNote,
      type: noteType,
      timestamp: new Date().toISOString(),
    };

    dispatch({
      type: 'UPDATE_CASE',
      payload: {
        id: currentCase.id,
        updates: {
          notes: [...(currentCase.notes || []), note],
        },
      },
    });

    setNewNote('');
    setNoteType('general');
  };

  // Herramientas disponibles
  const tools = [
    {
      id: 'diagnosis',
      title: 'Diagn贸stico',
      icon: 'stethoscope',
      color: '#9C27B0',
      onPress: () => {
        setShowToolsMenu(false);
        navigation.navigate('Diagnosis', { caseId: currentCase!.id });
      },
    },
    {
      id: 'tests',
      title: 'Tests',
      icon: 'clipboard',
      color: '#2196F3',
      onPress: () => {
        setShowToolsMenu(false);
        setShowTestsModal(true);
      },
      badge: state.user.coins,
    },
    {
      id: 'notes',
      title: 'Notas',
      icon: 'pencil',
      color: '#FF9800',
      onPress: () => {
        setShowToolsMenu(false);
        setShowNotesModal(true);
      },
      badge: currentCase?.notes?.length || 0,
    },
    {
      id: 'treatment',
      title: 'Tratamiento',
      icon: 'medkit',
      color: '#4CAF50',
      disabled: !currentCase?.diagnosis,
      onPress: () => {
        if (!currentCase?.diagnosis) {
          Alert.alert('Diagn贸stico requerido', 'Debes realizar un diagn贸stico antes de proponer un tratamiento.');
          return;
        }
        setShowToolsMenu(false);
        navigation.navigate('Treatment', { caseId: currentCase!.id });
      },
    },
  ];

  // Tests disponibles
  const availableTests = [
    { id: 'phq9', name: 'PHQ-9', cost: 5, type: 'Screening', description: 'Depresi贸n' },
    { id: 'gad7', name: 'GAD-7', cost: 5, type: 'Screening', description: 'Ansiedad generalizada' },
    { id: 'audit', name: 'AUDIT', cost: 5, type: 'Screening', description: 'Consumo de alcohol' },
    { id: 'bdi', name: 'BDI-II', cost: 15, type: 'Diagn贸stico', description: 'Inventario de depresi贸n de Beck' },
    { id: 'stai', name: 'STAI', cost: 15, type: 'Diagn贸stico', description: 'Ansiedad estado-rasgo' },
    { id: 'scid', name: 'SCID-5', cost: 15, type: 'Diagn贸stico', description: 'Entrevista cl铆nica estructurada' },
  ];

  const applyTest = (test: typeof availableTests[0]) => {
    if (state.user.coins < test.cost) {
      Alert.alert('Monedas insuficientes', `Necesitas ${test.cost} monedas para aplicar ${test.name}`);
      return;
    }

    Alert.alert(
      `Aplicar ${test.name}`,
      `驴Deseas aplicar ${test.name} por ${test.cost} monedas?`,
      [
        { text: 'Cancelar', style: 'cancel' },
        {
          text: 'Aplicar',
          onPress: () => {
            dispatch({ type: 'SPEND_COINS', payload: test.cost });

            // Simular resultado del test
            const score = Math.floor(Math.random() * 100);
            const result = {
              testId: test.id,
              testName: test.name,
              score,
              interpretation: score > 70 ? 'Puntuaci贸n alta - S铆ntomas significativos' :
                            score > 40 ? 'Puntuaci贸n moderada - Algunos s铆ntomas' :
                            'Puntuaci贸n baja - S铆ntomas m铆nimos',
              date: new Date().toISOString(),
            };

            dispatch({
              type: 'UPDATE_CASE',
              payload: {
                id: currentCase!.id,
                updates: {
                  batteryApplied: test.id,
                  batteryResults: result,
                },
              },
            });

            // A帽adir mensaje de resultados
            const resultMessage: Message = {
              id: generateId(),
              text: ` Resultados ${test.name}:\nPuntuaci贸n: ${score}/100\n${result.interpretation}`,
              sender: 'user',
              timestamp: new Date(),
              type: 'results',
            };
            addMessage(currentCase!.id, resultMessage);

            setShowTestsModal(false);
          },
        },
      ]
    );
  };

  // Renderizar mensaje
  const renderMessage = ({ item }: { item: Message }) => {
    const isUser = item.sender === 'user';

    return (
      <View style={[styles.message, isUser ? styles.messageSent : styles.messageReceived]}>
        <View style={[
          styles.messageBubble,
          isUser ? styles.bubbleSent : styles.bubbleReceived,
          item.type === 'results' && styles.bubbleResults,
        ]}>
          <Text style={[
            styles.messageText,
            isUser ? styles.textSent : styles.textReceived,
            item.type === 'results' && styles.textResults,
          ]}>
            {item.text}
          </Text>
          <View style={styles.messageFooter}>
            <Text style={[styles.messageTime, isUser ? styles.timeSent : styles.timeReceived]}>
              {new Date(item.timestamp).toLocaleTimeString('es-ES', {
                hour: '2-digit',
                minute: '2-digit',
              })}
            </Text>
            {item.score !== undefined && (
              <View style={[styles.scoreIndicator, { backgroundColor: item.color || getScoreColor(item.score) }]}>
                <Text style={styles.scoreText}>{item.score}</Text>
              </View>
            )}
          </View>
        </View>
      </View>
    );
  };

  // Color seg煤n score
  const getScoreColor = (score: number): string => {
    if (score >= 80) return '#4CAF50';
    if (score >= 60) return '#8BC34A';
    if (score >= 40) return '#FF9800';
    return '#f44336';
  };

  // Nueva sesi贸n
  const startNewSession = () => {
    if (!currentCase) return;

    dispatch({
      type: 'UPDATE_CASE',
      payload: {
        id: currentCase.id,
        updates: {
          sessions: currentCase.sessions + 1,
          questionsThisSession: 0,
        },
      },
    });

    setQuestionsCount(0);

    const responses = [
      "Regular, he estado pensando en lo que hablamos la 煤ltima vez...",
      "Bien, aunque todav铆a tengo algunas dudas sobre lo que me dijiste",
      "La semana ha sido dif铆cil, pero quiero seguir trabajando en esto",
      "Mejor, gracias. He estado reflexionando sobre nuestra conversaci贸n",
    ];

    const response = responses[Math.floor(Math.random() * responses.length)];
    const message: Message = {
      id: generateId(),
      text: response,
      sender: 'patient',
      timestamp: new Date(),
      type: 'text',
    };

    addMessage(currentCase.id, message);
    setShowToolsMenu(false);
  };

  // Panel lateral de informaci贸n
  const renderInfoPanel = () => (
    <Animated.View
      style={[
        styles.infoPanel,
        {
          transform: [{ translateX: panelPosition }],
        },
      ]}
    >
      <View style={styles.panelHeader}>
        <TouchableOpacity onPress={closePanel} style={styles.closePanelBtn}>
          <Icon name="chevron-right" size={20} color="#666" />
        </TouchableOpacity>
        <Text style={styles.panelTitle}>Expediente</Text>
      </View>

      <ScrollView style={styles.panelContent}>
        {/* Info del paciente */}
        <View style={styles.panelSection}>
          <View style={styles.patientHeader}>
            <View style={styles.patientAvatarLarge}>
              <Text style={styles.patientAvatarText}>
                {currentCase?.patient.avatar || ''}
              </Text>
            </View>
            <View style={styles.patientInfo}>
              <Text style={styles.patientName}>{currentCase?.patient.name}</Text>
              <Text style={styles.patientDetails}>
                {currentCase?.patient.age} a帽os  {currentCase?.patient.occupation}
              </Text>
            </View>
          </View>

          {/* Rapport */}
          <View style={styles.rapportContainer}>
            <Text style={styles.rapportLabel}>Rapport</Text>
            <View style={styles.rapportBar}>
              <View
                style={[
                  styles.rapportFill,
                  {
                    width: `${currentCase?.rapport || 70}%`,
                    backgroundColor: (currentCase?.rapport || 70) > 60 ? '#4CAF50' :
                                   (currentCase?.rapport || 70) > 30 ? '#FF9800' : '#f44336',
                  }
                ]}
              />
            </View>
            <Text style={styles.rapportValue}>{currentCase?.rapport || 70}%</Text>
          </View>
        </View>

        {/* S铆ntomas identificados */}
        <View style={styles.panelSection}>
          <Text style={styles.sectionTitle}>
            <Icon name="exclamation-triangle" size={14} color="#FF9800" /> S铆ntomas ({currentCase?.selectedSymptoms?.length || 0})
          </Text>
          {currentCase?.selectedSymptoms && currentCase.selectedSymptoms.length > 0 ? (
            <View style={styles.symptomsList}>
              {currentCase.selectedSymptoms.map((symptom, index) => (
                <View key={index} style={styles.symptomTag}>
                  <Text style={styles.symptomText}>{symptom}</Text>
                </View>
              ))}
            </View>
          ) : (
            <Text style={styles.emptyText}>No hay s铆ntomas identificados</Text>
          )}
        </View>

        {/* Notas */}
        <View style={styles.panelSection}>
          <Text style={styles.sectionTitle}>
            <Icon name="pencil" size={14} color="#2196F3" /> Notas ({currentCase?.notes?.length || 0})
          </Text>
          {currentCase?.notes && currentCase.notes.length > 0 ? (
            currentCase.notes.slice(-5).map((note) => (
              <View key={note.id} style={styles.noteItem}>
                <View style={[styles.noteTypeIndicator, {
                  backgroundColor: note.type === 'symptom' ? '#FF9800' :
                                  note.type === 'observation' ? '#2196F3' : '#9E9E9E'
                }]} />
                <Text style={styles.noteText}>{note.text}</Text>
              </View>
            ))
          ) : (
            <Text style={styles.emptyText}>No hay notas</Text>
          )}
        </View>

        {/* Resultados de tests */}
        {currentCase?.batteryResults && (
          <View style={styles.panelSection}>
            <Text style={styles.sectionTitle}>
              <Icon name="clipboard" size={14} color="#9C27B0" /> ltimo test
            </Text>
            <View style={styles.testResult}>
              <Text style={styles.testName}>{currentCase.batteryResults.testName}</Text>
              <Text style={styles.testScore}>
                Puntuaci贸n: {currentCase.batteryResults.score}/100
              </Text>
              <Text style={styles.testInterpretation}>
                {currentCase.batteryResults.interpretation}
              </Text>
            </View>
          </View>
        )}

        {/* Diagn贸stico */}
        {currentCase?.diagnosis && (
          <View style={styles.panelSection}>
            <Text style={styles.sectionTitle}>
              <Icon name="stethoscope" size={14} color="#4CAF50" /> Diagn贸stico
            </Text>
            <View style={styles.diagnosisBox}>
              <Text style={styles.diagnosisText}>{currentCase.diagnosis}</Text>
            </View>
          </View>
        )}
      </ScrollView>
    </Animated.View>
  );

  if (!currentCase) {
    return (
      <View style={styles.loadingContainer}>
        <Text>Cargando...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container} {...panResponder.panHandlers}>
      <LinearGradient
        colors={['#4A90E2', '#357abd']}
        style={styles.gradient}
      >
        <SafeAreaViewRN style={styles.header}>
          {/* Header */}
          <View style={styles.headerContent}>
            <TouchableOpacity
              style={styles.backButton}
              onPress={() => navigation.goBack()}
            >
              <Icon name="arrow-left" size={20} color="white" />
            </TouchableOpacity>
            <TouchableOpacity style={styles.headerInfo} onPress={openPanel}>
              <View style={styles.headerAvatar}>
                <Text style={styles.headerAvatarText}>{currentCase.patient.avatar}</Text>
              </View>
              <View style={styles.headerTextContainer}>
                <Text style={styles.headerName}>{currentCase.patient.name}</Text>
                <Text style={styles.headerStatus}>
                  Sesi贸n {currentCase.sessions + 1}  {5 - questionsCount} preguntas
                </Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity style={styles.headerButton} onPress={openPanel}>
              <Icon name="info-circle" size={22} color="white" />
            </TouchableOpacity>
          </View>
        </SafeAreaViewRN>

        <KeyboardAvoidingView
          style={styles.content}
          behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        >
          {/* Mensajes */}
          <FlatList
            ref={flatListRef}
            data={currentCase.messages}
            keyExtractor={(item) => item.id}
            renderItem={renderMessage}
            contentContainerStyle={styles.messagesContainer}
            showsVerticalScrollIndicator={false}
          />

          {/* Input flotante ovalado */}
          <View style={styles.floatingInputContainer}>
            {/* Indicador de fin de sesi贸n */}
            {questionsCount >= 5 && (
              <View style={styles.sessionEndBanner}>
                <Icon name="clock-o" size={16} color="#FF9800" />
                <Text style={styles.sessionEndText}>Sesi贸n completada</Text>
                <TouchableOpacity
                  style={styles.newSessionBtn}
                  onPress={startNewSession}
                >
                  <Text style={styles.newSessionBtnText}>Nueva sesi贸n</Text>
                </TouchableOpacity>
              </View>
            )}

            <View style={styles.inputRow}>
              {/* Bot贸n de herramientas */}
              <TouchableOpacity
                style={styles.toolsButton}
                onPress={() => setShowToolsMenu(true)}
              >
                <Icon name="plus" size={20} color="#4A90E2" />
              </TouchableOpacity>

              {/* Input ovalado */}
              <View style={styles.ovalInput}>
                <TextInput
                  style={styles.textInput}
                  value={messageInput}
                  onChangeText={setMessageInput}
                  placeholder={questionsCount >= 5 ? "Inicia nueva sesi贸n..." : "Escribe tu pregunta..."}
                  placeholderTextColor="#999"
                  multiline
                  maxLength={500}
                  editable={!isLoading && questionsCount < 5}
                />

                {/* Contador de preguntas */}
                <View style={styles.questionBadge}>
                  <Text style={styles.questionBadgeText}>{5 - questionsCount}</Text>
                </View>
              </View>

              {/* Bot贸n enviar */}
              <TouchableOpacity
                style={[
                  styles.sendButton,
                  (!messageInput.trim() || isLoading || questionsCount >= 5) && styles.sendButtonDisabled,
                ]}
                onPress={sendMessage}
                disabled={!messageInput.trim() || isLoading || questionsCount >= 5}
              >
                {isLoading ? (
                  <Icon name="spinner" size={18} color="white" />
                ) : (
                  <Icon name="paper-plane" size={18} color="white" />
                )}
              </TouchableOpacity>
            </View>

            {/* Indicador de escritura */}
            {isLoading && (
              <View style={styles.typingIndicator}>
                <Text style={styles.typingText}>{currentCase.patient.name} est谩 escribiendo...</Text>
              </View>
            )}
          </View>
        </KeyboardAvoidingView>

        {/* Overlay para panel */}
        {isPanelOpen && (
          <Animated.View
            style={[styles.panelOverlay, { opacity: overlayOpacity }]}
          >
            <TouchableOpacity style={styles.overlayTouch} onPress={closePanel} />
          </Animated.View>
        )}

        {/* Panel de informaci贸n */}
        {renderInfoPanel()}

        {/* Modal de herramientas */}
        <Modal
          transparent
          visible={showToolsMenu}
          animationType="slide"
          onRequestClose={() => setShowToolsMenu(false)}
        >
          <TouchableOpacity
            style={styles.modalOverlay}
            activeOpacity={1}
            onPress={() => setShowToolsMenu(false)}
          >
            <View style={styles.toolsModal}>
              <View style={styles.toolsModalHeader}>
                <Text style={styles.toolsModalTitle}>Herramientas</Text>
                <TouchableOpacity onPress={() => setShowToolsMenu(false)}>
                  <Icon name="times" size={22} color="#666" />
                </TouchableOpacity>
              </View>

              <View style={styles.toolsGrid}>
                {tools.map((tool) => (
                  <TouchableOpacity
                    key={tool.id}
                    style={[
                      styles.toolItem,
                      tool.disabled && styles.toolItemDisabled,
                    ]}
                    onPress={tool.onPress}
                    disabled={tool.disabled}
                  >
                    <View style={[styles.toolIcon, { backgroundColor: tool.color + '20' }]}>
                      <Icon name={tool.icon} size={24} color={tool.color} />
                      {tool.badge !== undefined && tool.badge > 0 && (
                        <View style={[styles.toolBadge, { backgroundColor: tool.color }]}>
                          <Text style={styles.toolBadgeText}>{tool.badge}</Text>
                        </View>
                      )}
                    </View>
                    <Text style={[styles.toolTitle, tool.disabled && styles.toolTitleDisabled]}>
                      {tool.title}
                    </Text>
                    {tool.disabled && (
                      <Icon name="lock" size={12} color="#999" style={styles.toolLock} />
                    )}
                  </TouchableOpacity>
                ))}
              </View>

              {/* Nueva sesi贸n */}
              <TouchableOpacity
                style={styles.newSessionButton}
                onPress={startNewSession}
              >
                <Icon name="refresh" size={18} color="#4A90E2" />
                <Text style={styles.newSessionText}>Nueva sesi贸n (+1)</Text>
              </TouchableOpacity>
            </View>
          </TouchableOpacity>
        </Modal>

        {/* Modal de notas */}
        <Modal
          transparent
          visible={showNotesModal}
          animationType="slide"
          onRequestClose={() => setShowNotesModal(false)}
        >
          <View style={styles.modalContainer}>
            <View style={styles.notesModal}>
              <View style={styles.notesModalHeader}>
                <Text style={styles.notesModalTitle}>Notas del caso</Text>
                <TouchableOpacity onPress={() => setShowNotesModal(false)}>
                  <Icon name="times" size={22} color="#666" />
                </TouchableOpacity>
              </View>

              {/* Selector de tipo */}
              <View style={styles.noteTypeSelector}>
                {(['general', 'symptom', 'observation'] as const).map((type) => (
                  <TouchableOpacity
                    key={type}
                    style={[
                      styles.noteTypeBtn,
                      noteType === type && styles.noteTypeBtnActive,
                    ]}
                    onPress={() => setNoteType(type)}
                  >
                    <Text style={[
                      styles.noteTypeBtnText,
                      noteType === type && styles.noteTypeBtnTextActive,
                    ]}>
                      {type === 'general' ? 'General' :
                       type === 'symptom' ? 'S铆ntoma' : 'Observaci贸n'}
                    </Text>
                  </TouchableOpacity>
                ))}
              </View>

              {/* Input de nota */}
              <View style={styles.noteInputContainer}>
                <TextInput
                  style={styles.noteInput}
                  value={newNote}
                  onChangeText={setNewNote}
                  placeholder="Escribe tu nota..."
                  multiline
                  maxLength={200}
                />
                <TouchableOpacity
                  style={[styles.addNoteBtn, !newNote.trim() && styles.addNoteBtnDisabled]}
                  onPress={addNote}
                  disabled={!newNote.trim()}
                >
                  <Icon name="plus" size={18} color="white" />
                </TouchableOpacity>
              </View>

              {/* Lista de notas */}
              <ScrollView style={styles.notesList}>
                {currentCase.notes && currentCase.notes.length > 0 ? (
                  [...currentCase.notes].reverse().map((note) => (
                    <View key={note.id} style={styles.noteListItem}>
                      <View style={[styles.noteTypeTag, {
                        backgroundColor: note.type === 'symptom' ? '#FF9800' :
                                        note.type === 'observation' ? '#2196F3' : '#9E9E9E'
                      }]}>
                        <Text style={styles.noteTypeTagText}>
                          {note.type === 'symptom' ? 'S' : note.type === 'observation' ? 'O' : 'G'}
                        </Text>
                      </View>
                      <View style={styles.noteContent}>
                        <Text style={styles.noteItemText}>{note.text}</Text>
                        <Text style={styles.noteTimestamp}>
                          {new Date(note.timestamp).toLocaleString('es-ES', {
                            day: '2-digit',
                            month: 'short',
                            hour: '2-digit',
                            minute: '2-digit',
                          })}
                        </Text>
                      </View>
                    </View>
                  ))
                ) : (
                  <Text style={styles.noNotesText}>No hay notas a煤n</Text>
                )}
              </ScrollView>
            </View>
          </View>
        </Modal>

        {/* Modal de tests */}
        <Modal
          transparent
          visible={showTestsModal}
          animationType="slide"
          onRequestClose={() => setShowTestsModal(false)}
        >
          <View style={styles.modalContainer}>
            <View style={styles.testsModal}>
              <View style={styles.testsModalHeader}>
                <Text style={styles.testsModalTitle}>Bater铆as de tests</Text>
                <View style={styles.coinsDisplay}>
                  <Icon name="circle" size={14} color="#FFD700" />
                  <Text style={styles.coinsText}>{state.user.coins}</Text>
                </View>
                <TouchableOpacity onPress={() => setShowTestsModal(false)}>
                  <Icon name="times" size={22} color="#666" />
                </TouchableOpacity>
              </View>

              <ScrollView style={styles.testsList}>
                <Text style={styles.testCategory}>Screening (5 monedas)</Text>
                {availableTests.filter(t => t.type === 'Screening').map((test) => (
                  <TouchableOpacity
                    key={test.id}
                    style={styles.testItem}
                    onPress={() => applyTest(test)}
                  >
                    <View style={styles.testInfo}>
                      <Text style={styles.testItemName}>{test.name}</Text>
                      <Text style={styles.testDescription}>{test.description}</Text>
                    </View>
                    <View style={styles.testCost}>
                      <Icon name="circle" size={12} color="#FFD700" />
                      <Text style={styles.testCostText}>{test.cost}</Text>
                    </View>
                  </TouchableOpacity>
                ))}

                <Text style={styles.testCategory}>Diagn贸stico (15 monedas)</Text>
                {availableTests.filter(t => t.type === 'Diagn贸stico').map((test) => (
                  <TouchableOpacity
                    key={test.id}
                    style={styles.testItem}
                    onPress={() => applyTest(test)}
                  >
                    <View style={styles.testInfo}>
                      <Text style={styles.testItemName}>{test.name}</Text>
                      <Text style={styles.testDescription}>{test.description}</Text>
                    </View>
                    <View style={styles.testCost}>
                      <Icon name="circle" size={12} color="#FFD700" />
                      <Text style={styles.testCostText}>{test.cost}</Text>
                    </View>
                  </TouchableOpacity>
                ))}
              </ScrollView>
            </View>
          </View>
        </Modal>
      </LinearGradient>
    </View>
  );
};

